import React from 'react';
import './Upload.css';

// #all the actions in the front end must be used to connect to the backend // form > function > jsonify > post to api

function Upload() {
    const [title, setTitle] = useState("");
    const [textinput, settextinput] = useState("");
    const [CheckedBox, setCheckedBox] = useState("");
    const [Message, setMessage] = useState("");


  let handleSubmit = async (e) => {
    e.preventDefault();
    try{
        
  let res = await fetch("app/upload",{
    method: "POST",
    body: JSON.stringify({
        title: title,
        textinput: textinput,
        checkedbox: checkedbox,
    }),
  });
  let resJson =  await res.json();
  if(res.status == 200){
    setTitle("");
    settextinput("");
    setMessage("Crawled successfully")
  }
  else{
    setMessage("Some error occured");
  }
    }

  
catch(err){
    console.log(err);
  }


// const Upload = () => {
    // const [key, setKey] = useState('home');
  return(

         <form onSubmit={handleSubmit}>
        <div>
        <input type = "text" value={title} onChange={(e)=>setTitle(e.target.value)} required autofocus/>
        <label>Title </label>
        </div>
        <div>
        <textarea name="message" value ={email} onChange={(e)=>setMessage(e.target.value)}></textarea>
            <label>Text </label>
        </div>
        <div>
        <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"/>
        <label for="vehicle1"> Option1</label>
    </div>
    <div>
<input type="checkbox" id="vehicle2" name="vehicle2" value="Car"/>
<label for="vehicle2"> Option2</label>
</div>
      
      
        <input type="submit" value="Submit"/>
        </form>
    )
  }
}


  export default Upload;
