          const UploadUsingDropzone = (props) => {
            const { files, setFiles, uploadFiles, deleteFile, setConfig, config } = props;
            const file = (files.map(file, index)) ;
            const customColorTheme = deepMerge(base, {
                global: {
                  colors: {
                    icons: '#333333',
                  },
                },
                icon: {
                  extend: css`
                    ${(props) =>
                      props.color === 'brand' &&
                      `
                      fill: #64FFDA;
                      stroke: #64FFDA;
                    `}
                  `,}
                    })
                    // the modal 
                    const [show, setShow] = useState(false);
                  
                    // const handleClose = () => setShow(false);
                    // const handleShow = () => setShow(true);
                  
                    const handleShow = () => {
                  
                    }
                  
                    const handleClose = () => {
                      
                    }
                  
                    // check file type
                    
                    const isJson = (fileType) => {
                      if (fileType == 'json') {
                        return true;
                      } else {
                        return false;
                      }
                    };
                  
                    // for process button 
                    const handleClick = (file) => {
                      if (file.length===1){
                        var fileType = file[0].path.split('.').slice(-1)[0];
                        if (isJson(fileType)) {
                          uploadFiles();
                        } else {
                          handleShow();
                        }
                      } else {
                        handleShow();
                      }
                      // console.log(file)
                    }  
                  
                    // for button style
                    const useStyles = makeStyles(theme => ({
                      outlined: {
                        color: 'rgb(3, 155, 144)',
                        border: '1px solid',
                        margin: '8px',
                        "&:hover": {
                          color: '#b1fdf8'
                        }
                      }
                    }));
                    const classes = useStyles();
                
              
            return (
              <React.Fragment>
              <div className="uploaded-file-container" key={file.index}>
                <div className="uploaded-file">
                  <div theme={customColorTheme}>
                      file.name.substring(
                      file.name.lastIndexOf('.') + 1,
                      file.name.length
                    ) === 'pdf' ? (
                      <DocumentPdf color="brand" />
                    ) : (
                      <DocumentTxt color="brand" />
                    )
                     
                  </div>
                  <div className="uploaded-file-details">
                    <span>File Name: {file.name}</span>
                    <span>File Size: {(file.size / 1000000).toFixed(2)}MB</span>
                  </div>
                </div>
                <div className="delete-uploaded-file">
                  <TiDeleteOutline
                    size={30}
                    color="red"
                    onClick={() => deleteFile(file.index)}
                  />
                </div>
              </div>,
              </React.Fragment>
            );
          
        }
          
       

export default UploadUsingDropzone;
